    /* PORTC8 (pin 65) is configured as PTC8 */
    PORT_SetPinMux(BOARD_INITPINS_LCD_D6_PORT, BOARD_INITPINS_LCD_D6_PIN, kPORT_MuxAsGpio);

    /* PORTC9 (pin 66) is configured as PTC9 */
    PORT_SetPinMux(BOARD_INITPINS_LCD_D7_PORT, BOARD_INITPINS_LCD_D7_PIN, kPORT_MuxAsGpio);

    /* PORTD0 (pin 73) is configured as TPM0_CH0 */
    PORT_SetPinMux(BOARD_INITPINS_ULTRA_ECHO_F_PORT, BOARD_INITPINS_ULTRA_ECHO_F_PIN, kPORT_MuxAlt4);

    /* PORTD2 (pin 75) is configured as PTD2 */
    PORT_SetPinMux(BOARD_INITPINS_R_ENCODER_PORT, BOARD_INITPINS_R_ENCODER_PIN, kPORT_MuxAsGpio);

    /* Interrupt configuration on PORTD2 (pin 75): Interrupt on rising edge */
    PORT_SetPinInterruptConfig(BOARD_INITPINS_R_ENCODER_PORT, BOARD_INITPINS_R_ENCODER_PIN, kPORT_InterruptRisingEdge);

    /* PORTD3 (pin 76) is configured as PTD3 */
    PORT_SetPinMux(BOARD_INITPINS_L_ENCODER_PORT, BOARD_INITPINS_L_ENCODER_PIN, kPORT_MuxAsGpio);

    /* Interrupt configuration on PORTD3 (pin 76): Interrupt on rising edge */
    PORT_SetPinInterruptConfig(BOARD_INITPINS_L_ENCODER_PORT, BOARD_INITPINS_L_ENCODER_PIN, kPORT_InterruptRisingEdge);

    /* PORTD4 (pin 77) is configured as PTD4 */
    PORT_SetPinMux(BOARD_INITPINS_WCET_MSR_PORT, BOARD_INITPINS_WCET_MSR_PIN, kPORT_MuxAsGpio);

    /* PORTD5 (pin 78) is configured as PTD5 */
    PORT_SetPinMux(BOARD_INITPINS_LCD_EN_PORT, BOARD_INITPINS_LCD_EN_PIN, kPORT_MuxAsGpio);

    /* PORTE0 (pin 1) is configured as I2C1_SDA */
    PORT_SetPinMux(BOARD_INITPINS_MPU_SDA_PORT, BOARD_INITPINS_MPU_SDA_PIN, kPORT_MuxAlt6);

    /* PORTE1 (pin 2) is configured as I2C1_SCL */
    PORT_SetPinMux(BOARD_INITPINS_MPU_SCL_PORT, BOARD_INITPINS_MPU_SCL_PIN, kPORT_MuxAlt6);

    /* PORTE2 (pin 3) is configured as PTE2 */
    PORT_SetPinMux(BOARD_INITPINS_ULTRA_TRIG_R_PORT, BOARD_INITPINS_ULTRA_TRIG_R_PIN, kPORT_MuxAsGpio);

    /* PORTE21 (pin 14) is configured as PTE21 */
    PORT_SetPinMux(BOARD_INITPINS_IR_SS1_PORT, BOARD_INITPINS_IR_SS1_PIN, kPORT_MuxAsGpio);

    /* PORTE22 (pin 15) is configured as PTE22 */
    PORT_SetPinMux(BOARD_INITPINS_IR_SS2_PORT, BOARD_INITPINS_IR_SS2_PIN, kPORT_MuxAsGpio);

    /* PORTE23 (pin 16) is configured as PTE23 */
    PORT_SetPinMux(BOARD_INITPINS_IR_SS3_PORT, BOARD_INITPINS_IR_SS3_PIN, kPORT_MuxAsGpio);

    /* PORTE29 (pin 21) is configured as PTE29 */
    PORT_SetPinMux(BOARD_INITPINS_IR_SS4_PORT, BOARD_INITPINS_IR_SS4_PIN, kPORT_MuxAsGpio);

    /* PORTE3 (pin 4) is configured as PTE3 */
    PORT_SetPinMux(BOARD_INITPINS_ULTRA_TRIG_F_PORT, BOARD_INITPINS_ULTRA_TRIG_F_PIN, kPORT_MuxAsGpio);

    /* PORTE30 (pin 22) is configured as PTE30 */
    PORT_SetPinMux(BOARD_INITPINS_IR_SS5_PORT, BOARD_INITPINS_IR_SS5_PIN, kPORT_MuxAsGpio);

